        private static Rectangle[,] Acid { get; } = new Rectangle[tiles.GetLength(0), tiles.GetLength(1)];
        private static Rectangle[,] Finish { get; } = new Rectangle[tiles.GetLength(0), tiles.GetLength(1)];





        public Map()
        {
            _target = new(Globals.GraphicsDevice, tiles.GetLength(1) * TILE_SIZE, tiles.GetLength(0) * TILE_SIZE);

            var tile1tex = Globals.Content.Load<Texture2D>("Tiles/tile_a");
            var tile2tex = Globals.Content.Load<Texture2D>("Tiles/tileb1");
            var tile3tex = Globals.Content.Load<Texture2D>("Tiles/tile3");
            var tile4tex = Globals.Content.Load<Texture2D>("Tiles/tile_4");
            var tile5tex = Globals.Content.Load<Texture2D>("Tiles/tile_5");
            var tile6tex = Globals.Content.Load<Texture2D>("Tiles/tile_6");
            var tile7tex = Globals.Content.Load<Texture2D>("Tiles/tile_5a");
            var finisher = Globals.Content.Load<Texture2D>("Colours/black");
            var grey = Globals.Content.Load<Texture2D>("Tiles/gold");
            var black = Globals.Content.Load<Texture2D>("Tiles/end");

            Globals.GraphicsDevice.SetRenderTarget(_target);
            Globals.GraphicsDevice.Clear(Color.Transparent);
            Globals.SpriteBatch.Begin();

            for (int x = 0; x < tiles.GetLength(0); x++)
            {
                for (int y = 0; y < tiles.GetLength(1); y++)
                {
                    if (tiles[x, y] == 0) continue;
                    var posX = y * TILE_SIZE;
                    var posY = x * TILE_SIZE;
                    //var tex = tiles[x, y] == 1 ? tile1tex : tile2tex;
                    Texture2D tex;

                    if (tiles[x, y] == 1)
                    {
                        tex = tile1tex;
                    }
                    else if (tiles[x, y] == 2)
                    {
                        tex = tile2tex;
                    }
                    else if (tiles[x, y] == 3)
                    {
                        tex = tile3tex;
                    }
                    else if (tiles[x, y] == 4)
                    {
                        tex = tile4tex;
                    }
                    else if (tiles[x, y] == 5)
                    {
                        tex = tile5tex;
                    }
                    else if (tiles[x, y] == 6)
                    {
                        tex = tile6tex;
                    }
                    else if (tiles[x, y] == 7)
                    {
                        tex = tile7tex;
                    }
                    else if (tiles[x, y] == 8)
                    {
                        tex = black;
                    }
                    else if (tiles[x, y] == 10)
                    {
                        tex = finisher;
                    }
                    else
                    {
                        tex = grey;
                    }
                    Colliders[x, y] = new(posX, posY, TILE_SIZE, TILE_SIZE);
                    Acid[x,y] = new(posX, posY, TILE_SIZE,TILE_SIZE);
                    Finish[x,y] = new(posX,posY, TILE_SIZE, TILE_SIZE);
                    Globals.SpriteBatch.Draw(tex, new Vector2(posX, posY), Color.White);
                }
            }

            Globals.SpriteBatch.End();
            Globals.GraphicsDevice.SetRenderTarget(null);
        }

        public static List<Rectangle> GetNearestColliders(Rectangle bounds)
        {
            int leftTile = (int)Math.Floor((float)bounds.Left / TILE_SIZE);
            int rightTile = (int)Math.Ceiling((float)bounds.Right / TILE_SIZE) - 1;
            int topTile = (int)Math.Floor((float)bounds.Top / TILE_SIZE);
            int bottomTile = (int)Math.Ceiling((float)bounds.Bottom / TILE_SIZE) - 1;

            leftTile = MathHelper.Clamp(leftTile, 0, tiles.GetLength(1) - 1);
            rightTile = MathHelper.Clamp(rightTile, 0, tiles.GetLength(1) - 1);
            topTile = MathHelper.Clamp(topTile, 0, tiles.GetLength(0) - 1);
            bottomTile = MathHelper.Clamp(bottomTile, 0, tiles.GetLength(0) - 1);


            List<Rectangle> result = new();

            for (int x = topTile; x <= bottomTile; x++)
            {
                for (int y = leftTile; y <= rightTile; y++)
                {
                    if (tiles[x, y] == 1 || tiles[x, y] == 2) { result.Add(Colliders[x, y]); }
                }
            }

            return result;
        }

        public static List<Rectangle> GetNearestAcids(Rectangle bounds)
        {
            int leftTile = (int)Math.Floor((float)bounds.Left / TILE_SIZE);
