            topTile = MathHelper.Clamp(topTile, 0, tiles.GetLength(0) - 1);
            bottomTile = MathHelper.Clamp(bottomTile, 0, tiles.GetLength(0) - 1);


            List<Rectangle> result = new();

            for (int x = topTile; x <= bottomTile; x++)
