            return result;
        }

        public static List<Rectangle> GetNearestFinisher(Rectangle bounds)
        {
            int leftTile = (int)Math.Floor((float)bounds.Left / TILE_SIZE);
            int rightTile = (int)Math.Ceiling((float)bounds.Right / TILE_SIZE) - 1;
            int topTile = (int)Math.Floor((float)bounds.Top / TILE_SIZE);
            int bottomTile = (int)Math.Ceiling((float)bounds.Bottom / TILE_SIZE) - 1;

            leftTile = MathHelper.Clamp(leftTile, 0, tiles.GetLength(1) - 1);
            rightTile = MathHelper.Clamp(rightTile, 0, tiles.GetLength(1) - 1);
            topTile = MathHelper.Clamp(topTile, 0, tiles.GetLength(0) - 1);
            bottomTile = MathHelper.Clamp(bottomTile, 0, tiles.GetLength(0) - 1);


            List<Rectangle> result = new();

            for (int x = topTile; x <= bottomTile; x++)
            {
                for (int y = leftTile; y <= rightTile; y++)
                {
                    if (tiles[x,y]==10) { result.Add(Finish[x, y]); }
                }
            }

            return result;
        }




